<?php/*** add_outword.php* Part of Admin panel for displaying / adding / removing outwords for a keyword combination* @author Twitter Activist <tcpdemo2@gmail.com>* @license GNU Public License* @version 1.0*/include('header.php');include('config.php');session_start();if(!isset($_SESSION['user_id'])){    die();}$query="select * from collection_words";$stmt=$db->prepare($query);$stmt->execute();$row=$stmt->fetch();$out_words=$row['out_words'];?><div class="row">	<?php include('left_sidebar.php'); ?>	<div class="span9">					<h1>						Add Outwords					</h1>				<?php				if(isset($_SESSION['error'])){ ?>		<div class="alert alert-danger"><?php echo $_SESSION['error']; ?></div>			<?php } unset($_SESSION['error']); ?>			<form name="form1" id="add_rmuser" class="form-horizontal" action="out_wordssave.php" onsubmit="return validateForm()" method="post">				<fieldset>					<legend></legend>					<div class="control-group">						<label class="control-label" for="input01">Keyword name</label>						<div class="controls">							<select id="keywords" name="keywords" onchange="dokeywords();">							<?php								$query="select * from collection_words";								$stmt=$db->prepare($query);								$stmt->execute();								foreach($stmt as $row)								{								echo "<option value=\" " . $row['id'] .'">' .$row['words'] ."</option>";															}							?>							</select>						</div>									<br/><br/>								<label class="control-label" for="input02">Out Words</label>						<div class="controls">							<input type="text" id="outwords" name = "outwords" class="input-xlarge" id="input02" placeholder="Separate by comma optional" value="<?php echo $out_words?>"/>						</div>					</div>											<div class="form-actions">						<button name="kname" type="submit" class="btn btn-primary">Save</button> <a href="keyword_list.php" class="btn">Cancel</a>					</div>				</fieldset>			</form>	</div></div>		<script src="js/jquery.min.js"></script>		<script src="js/bootstrap.min.js"></script>		<script>			function dokeywords()			{				var keywordid=document.getElementById('keywords').value;				urltopass='keyword_info.php?keyword='+keywordid;				$.ajax({						url: urltopass,						dataType: 'json',						success: function (data) {							$('#outwords').val(data[0]['out_words']);						}				});			}		</script>